// Start variables
$total = 0

// Loop through item 800.12345 quantities and sum
FOREACH %%Quantities/BillRecord%% IN %%Quantities%% ,
    IF %%Quantities/BillRecord/ItemCode%% == "800.12345"
    THEN 
        $total = $total + %%Quantities/BillRecord/Quantity%%
    ENDIF 
NEXT

// Calculate total cost
$total_cost = $total * 21.5

// Export item 027.12345 to cost centre 27 and load 2
EXPORT("027.12345", $total_cost, 27, 2, 1, "Exported total cost")
